function [y1,y2,y3] = sampleFunction(x,y)  y1 = x^2 + y^2 + 1  y2 = (x+y)^3  y3 = x^3 + y^3 + 4  end;[a,b,c] = sampleFunction(5,4)#####################################################################################This function is used to find the value of cost function for a fixed value of theta#Inputs : feature Matrix X,  Label Y, Theta#Output : J(Theta)# Operation performed# Hypothesis h(theta) = theta(0)*X(0) + theta(1)*X(1)# Cost Function J(theta) = 1/2m * (h(theta)-Y).^2# This is not the minimal value of the cost function####################################################################################function J = costFunction(X,Y,theta)  % X - Feature Matrix  % Y - Label  m = size(X,1);    % Training Exampl size  predictions = X*theta; % Hypothesis h(theta)   sqerrors = (predictions-Y).^2; %Squared Error  J = 1/(2*m) * sum(sqerrors);end;X = [1 1;1 2;1 3];Y = [1;2;3]theta = [100;3]disp(costFunction(X,Y,theta))